mapper.select:
SELECT logo
FROM tb_product
WHERE id = #{id}

mapper.insert:
insert into tb_product (id, name, summary, 
  yprice, price, logo, banner, detail, sort
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{summary,jdbcType=VARCHAR}, 
  #{yprice,jdbcType=DECIMAL}, #{price,jdbcType=DECIMAL}, #{logo,jdbcType=LONGVARBINARY}, #{banner,jdbcType=VARCHAR},
  #{detail,jdbcType=VARCHAR}, #{sort}
  )
